import { Component } from '@angular/core';
import { FileUploader } from 'ng2-file-upload';
import { HttpClient, HttpHeaders, HttpResponse } from '@angular/common/http'

@Component({
  selector: 'app-upload',
  templateUrl: './upload.component.html',
  styleUrls: ['./upload.component.css']
})
export class UploadComponent {

  urlApi: string = 'http://localhost:3000/api/upload';
  constructor(private http: HttpClient) { }
  public uploader: FileUploader = new FileUploader({
    url: this.urlApi,
    itemAlias: 'fieldName',
  });
  ngOnInit() {
    this.uploader.onAfterAddingFile = (fichier: any) => {
      // si la requête n'utilise pas des identifiants
      fichier.withCredentials = false;
      console.log('Fichier ajouté:', fichier);
    };
    this.uploader.onCompleteItem = (item: any, status: any) => {
      console.log('Fichier envoyé:', item);
    };
  }

  downloadFile(url: string): any {

    // fetch(url, { method: 'get', mode: 'no-cors', referrerPolicy: 'no-referrer' })
    //   .then(res => res.blob())
    //   .then(res => {
    //     const aElement = document.createElement('a');
    //     // aElement.setAttribute('download', 'test');
    //     const href = URL.createObjectURL(res);
    //     aElement.href = href;
    //     // aElement.setAttribute('href', href);
    //     aElement.setAttribute('target', '_blank');
    //     aElement.click();
    //     URL.revokeObjectURL(href);
    //   });
    // return this.http.get('http://localhost:3000/documents/Capture d’écran de 2022-12-06 10-33-34.png', { responseType: 'blob' });
    const baseUrl = 'http://localhost:3000/documents/Capture d’écran de 2022-12-06 10-33-34.png';
    const token = 'my JWT';
    const headers = new HttpHeaders().set('authorization', 'Bearer ' + token);
    this.http.get(baseUrl, { headers, responseType: 'blob' as 'json' }).subscribe(
      (response: any) => {
        let dataType = response.type;
        let binaryData = [];
        binaryData.push(response);
        let downloadLink = document.createElement('a');
        downloadLink.href = window.URL.createObjectURL(new Blob(binaryData, { type: dataType }));

        downloadLink.setAttribute('download', 'nomfichier');
        downloadLink.setAttribute('target', '_blank');

        document.body.appendChild(downloadLink);
        downloadLink.click();
      }
    )
  }



}
